mapper.select:
SELECT COUNT(*) FROM LANDMARK
WHERE LOCATION_TYPE = ${locationNum}
AND LM_FL = 'N'

		SELECT * FROM LAND_IMG
    	WHERE LM_NO = #{landMakrNo}
    	ORDER BY LM_IMG_LEVEL

mapper.insert:
INSERT INTO LAND_IMG SELECT SEQ_LM_IMG_NO.NEXTVAL IMG_NO, A.* FROM(
SELECT #{img.landMarkNo} LM_NO, #{img.landMarkReName} LM_IMG_RENAME, #{img.landMarkImageLV} LM_IMG_LEVEL FROM DUAL
) A

mapper.delete:
UPDATE LAND_IMG SET
WHERE LM_NO = #{landMarkNo}
AND LM_IMG_LEVEL IN (#{deleteList})

mapper.update:
UPDATE LAND_IMG SET
LM_IMG_RENAME = #{landMarkReName}
WHERE LM_NO = #{landMarkNo}
AND LM_IMG_LEVEL = #{landMarkImageLV}

mapper.select:
SELECT COUNT(*)
FROM BOOKMARK_LM
WHERE USER_NO = #{loginNo}
AND LM_NO = #{landmarkNo}

mapper.delete:
DELETE FROM BOOKMARK_LM
WHERE USER_NO = #{loginNo}
AND LM_NO = #{landmarkNo}

mapper.select:
SELECT AVG(RATE)
FROM LANDMARK_RATE
WHERE LM_NO = #{landmarkNo}

mapper.delete:
DELETE FROM LANDMARK_RATE
WHERE USER_NO = #{userNo}
AND LM_NO = #{sLandmarkNo}

mapper.select:
SELECT COUNT(*)
FROM LANDMARK_RATE
WHERE USER_NO = #{userNo}
AND LM_NO = #{landmarkNo}
